const headerMenuItem=document.querySelectorAll(".header__item-link"),menuBtn=document.querySelector(".header__menu-icon"),menu=document.querySelector(".header__nav"),circleItem=document.querySelectorAll(".content__box-img"),length=circleItem.length,arc=2*Math.PI*(1/length),radius=50,searchInput=document.querySelector(".content__search-input");function openMenu(){menu.classList.toggle("active"),menuBtn.classList.toggle("open")}function startFanAnimation(){setTimeout((()=>{document.querySelector(".content__right-box").classList.add("start__fan"),document.querySelectorAll(".content__box-img").forEach((e=>{e.classList.add("start__spin")}))}),1e3)}async function getDescription(){try{const e=await fetch("https://baconipsum.com/api/?type=lucky");if(e.ok){const t=(await e.json())[0];document.querySelector(".content__text").textContent=t}else console.error("Ошибка при выполнении запроса:",e.status)}catch(e){console.error("Ошибка:",e)}}headerMenuItem.forEach((e=>{e.addEventListener("click",(()=>{headerMenuItem.forEach((e=>{e.classList.remove("active__link")})),e.classList.add("active__link")}))})),menuBtn.addEventListener("click",openMenu),window.addEventListener("load",(e=>{e.preventDefault(),setTimeout((()=>{for(let e=0;e<length;e++){const t=e*arc,n=50*Math.cos(t),c=50*Math.sin(t);setTimeout((()=>{circleItem[e].style.left=50+n+"%",circleItem[e].style.top=50+c+"%",e===length-1&&startFanAnimation()}),400*e)}}),3e3)})),searchInput.addEventListener("input",(function(){const e=this.value;if(e.length<4)this.setCustomValidity("Минимум 4 символа");else if(e.length>12)this.setCustomValidity("Максимум 12 символов");else{["!","@","#","$","%","^","&","*","(",")"].some((t=>e.includes(t)))?this.setCustomValidity("Запрещенные символы: ! @ # $ % ^ & * ( )"):this.setCustomValidity("")}})),getDescription();